<?phprequire_once('../config/config.php');class Database {    private $connect;    public function __construct() {        try {            $this->connect = new PDO ("mysql:host=".HOST.";port=3306;dbname=".DB."","".USER."","".PASS."");		} catch ( PDOException $Exception ) {			echo "<p>N&atilde;o foi possivel conectar-se ao servidor de Banco de dados.</p>\n".			"<p><strong>Erro: " . htmlentities($Exception->getMessage()) . "</strong></p>\n";			exit();      			}         	}       	public function conn() {		return $this->connect;    	}                	public function close() {		$this->connect = null;    	}         }?>